<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Cargos_model extends MY_Model{	public function __construct()	{		parent::__construct();		$this->table_name = "cargos";		$this->msg_name = 'Cargo';		$this->id_name = 'id';		$this->columnas = array('id', 'descripcion');		$this->fields = array(			array('name' => 'descripcion', 'label' => 'Descripción', 'maxlength' => '200', 'required' => TRUE)		);		$this->requeridos = array('descripcion');		$this->unicos = array('descripcion');	}	/**	 * _can_delete: Devuelve true si puede eliminarse el registro.	 *	 * @param int $delete_id	 * @return bool	 */	protected function _can_delete($delete_id)	{		if ($this->db->where('cargo_id', $delete_id)->count_all_results('cargos_usuarios') > 0)		{			$this->_set_error('No se ha podido eliminar el registro de ' . $this->msg_name . '. Verifique que no esté asociado a cargos de usuarios.');			return FALSE;		}		if ($this->db->where('cargo_id', $delete_id)->count_all_results('firmas_circuitos') > 0)		{			$this->_set_error('No se ha podido eliminar el registro de ' . $this->msg_name . '. Verifique que no esté asociado a firmas de circuitos.');			return FALSE;		}		return TRUE;	}}/* End of file Cargos_model.php *//* Location: ./application/modules/expedientes/models/Cargos_model.php */